package AddHocPrograms.AddHocProgramsArtifact;

class ModDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int iresult, irem;
		double dresult, drem;
		
		iresult = 10 / 3;
		irem = 10 % 3;
		
		dresult = 10.0 / 3.0;
		drem = 10.0 % 3.0;

		
		System.out.println("For 10 / 3 in int the result is " + iresult + " and remainder is :" + irem);
		System.out.println("For 10 / 3 in double the result is " + dresult + " and remainder is :" + drem);
	}

}
